import React, { Component } from 'react';
import { connect } from 'react-redux';
import fetchLeagueListAPI from '../fetchApi/fetchLeague';
import { getLeagueList, getLeagueListPending, getLeagueListError } from '../reducer/reducer'
import '../App.css';
class App extends Component {
  componentDidMount() {
    this.props.fetchListLeague();
  }

  render() {
    const { leagueList, error, pending } = this.props;
    return (
      <div id="header" className="header">
      <div className="header-content">
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="text-container">
                <h1>MOBILE APP <br />FOR <span id="js-rotating">DESIGNERS, MARKETERS, DEVELOPERS</span></h1>
                <p className="p-large">Leno is one of the easiest and feature packed marketing automation apps in the market. Download it today!</p>
                <a className="btn-solid-lg page-scroll" href="#your-link"><i className="fab fa-apple" />APP STORE</a>
                <a className="btn-solid-lg page-scroll" href="#your-link"><i className="fab fa-google-play" />PLAY STORE</a>
              </div>
            </div> {/* end of col */}
            <div className="col-lg-6">
              <div className="image-container">
                <img className="img-fluid" src="images/header-iphone.png" alt="alternative" />
              </div> 
            </div> 
          </div> 
        </div> 
      </div> 
    </div>
      )
  }
}
const mapStateToProps = (state, ownProps) => {
  return {
    error: getLeagueListError(state),
    pending: getLeagueListPending(state),
    leagueList: getLeagueList(state)
  }
}
const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    fetchListLeague: () => {
      dispatch(fetchLeagueListAPI())
    }
  }
}
// const mapDispatchToProps = dispatch => bindActionCreators({
//   fetchProducts: fetchLeagueList
// }, dispatch)
export default connect(mapStateToProps, mapDispatchToProps)(App)
